// SPDX-License-Identifier: AGPL-1.0

pragma solidity 0.7.5;

library Math {
    function mul(
        uint256 a,
        uint256 b,
        string memory overflowError
    ) internal pure returns (uint256 c) {
        require(b == 0 || a == 0 || ((c = a * b) / b) == a, overflowError);
    }

    function add(
        uint256 a,
        uint256 b,
        string memory overflowError
    ) internal pure returns (uint256 c) {
        require((c = a + b) >= a, overflowError);
    }

    function sub(
        uint256 a,
        uint256 b,
        string memory underflowError
    ) internal pure returns (uint256 c) {
        require((c = a - b) <= a, underflowError);
    }

    function mul18(
        uint256 a18,
        uint256 b18,
        string memory overflowError
    ) internal pure returns (uint256) {
        return mul(a18, b18, overflowError) / 10**18;
    }

    function div18(
        uint256 a18,
        uint256 b18,
        string memory overflowError
    ) internal pure returns (uint256) {
        return mul(a18, 10**18, overflowError) / b18;
    }

    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a <= b ? a : b;
    }

    function max(uint256 a, uint256 b) internal pure returns (uint256) {
        return a >= b ? a : b;
    }

    function smin(int256 a, int256 b) internal pure returns (int256) {
        return a <= b ? a : b;
    }

    function smax(int256 a, int256 b) internal pure returns (int256) {
        return a >= b ? a : b;
    }

    function sqrt(uint256 a) internal pure returns (uint256 c) {
        uint256 tmp = (a + 1) / 2;
        c = a;
        while (tmp < c) {
            c = tmp;
            tmp = ((a / tmp) + tmp) / 2;
        }
    }
}

hardhat-deploy/solc_0.7/proxy/Proxied.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;

abstract contract Proxied {
    /// @notice to be used by initialisation / postUpgrade function so that only the owner can execute them
    /// It also allows these functions to be called inside a contructor when the contract
    /// is meant to be used without proxy
    modifier proxied() {
        address ownerAddress = _owner();
        // With hardhat-deploy proxies
        // the ownerAddress is zero only for the implementation contract
        // if the implementation contract want to be used as standalone
        // it simply has to execute the `proxied` function
        // This ensure the ownerAddress is never zero post deployment
        if (ownerAddress == address(0)) {
            // ensure can not be called twice when used outside of proxy : no admin
            // solhint-disable-next-line security/no-inline-assembly
            assembly {
                sstore(
                    0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103,
                    0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF
                )
            }
        } else {
            require(msg.sender == ownerAddress);
        }
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == _owner(), "NOT_AUTHORIZED");
        _;
    }

    function _owner() internal view returns (address ownerAddress) {
        // solhint-disable-next-line security/no-inline-assembly
        assembly {
            ownerAddress := sload(0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103)
        }
    }
}

